ot_values = %w'Saloon_Standard MPV_Standard Saloon_Corporate Taxi_Standard MPV_Standard_Black'
vehicle_ids = DB[:vehicles].where(value: ot_values).select_hash(:value, :id)

vehicle_vendor_ids = DB[:vehicle_vendors].select_hash(:key, :id)

vehicle_vendors_vehicles_mapping.each do |vendor_key, vehicle_keys|
  vendor_id = vehicle_vendor_ids[vendor_key]

  next if vendor_id.blank?

  Seeds.seed_once(DB[:vehicle_vendors_vehicles], :vehicle_id, :vehicle_vendor_id,
    vehicle_keys.map{ |key| {vehicle_id: vehicle_ids[key], vehicle_vendor_id: vendor_id} }
  )
end
